import java.util.HashSet;
import java.util.Set;

public class Day6 {
    public static void main(String[] args) {
        String input = "";
        int startOfPacket = findStart(input, 4, 0);
        System.out.println("Start of packet: " + startOfPacket);
        int startOfMessage = findStart(input, 14, startOfPacket - 4);
        System.out.println("Start of message: " + startOfMessage);
    }

    private static int findStart(String input, int numDistinct, int startFrom) {
        for(int i = numDistinct + startFrom; i < input.length(); i++) {
            Set<Character> seen = new HashSet<>();
            for(int j = 0; j < numDistinct; j++) {
                seen.add(input.charAt(i - j));
            }
            if(seen.size() == numDistinct) {
                return i + 1;
            }
        }
        return 0;
    }
}
